import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  let increase = 10;
  const [student, setStudent] = useState([]);
  const loadStudent = () => {
    axios.get("http://localhost:5000/api/getInfo").then(({ data }) => {
      let fetchedData = data.slice(0, increase);
      const allStudent = [];
      fetchedData.forEach((s) => {
        allStudent.push({ id: s.id, name: s.email, email: s.name });
      });
      setStudent(allStudent);
    });
  };

  const handleScroll = (e) => {
    console.log("Hi");
    if (
      window.innerHeight + e.target.documentElement.scrollTop + 1 >=
      e.target.documentElement.scrollHeight
    ) {
      console.log("At the bottom of the page!");
    }
  };

  useEffect(() => {
    loadStudent();
    window.addEventListener("scroll", handleScroll);
  }, []);
  return (
    <div>
      {student.map((p) => {
        return <div key={Math.random() * 200 + 1}>{p.id}</div>;
      })}
    </div>
  );
};

export default App;
